import React, { useState } from 'react';

function Predict() {
  const [input, setInput] = useState('');
  const [result, setResult] = useState('');

  const handlePredict = () => {
    if (input.trim() === '') return;
    setResult(`ðŸ“Š Prediction: ${input.split('').reverse().join('')}`);
  };

  return (
    <div className="min-h-screen bg-red-50 text-red-900 flex flex-col items-center px-6 py-16">
      <h2 className="text-4xl font-bold mb-6">Prediction Tool</h2>
      <input
        type="text"
        placeholder="Enter your query..."
        value={input}
        onChange={(e) => setInput(e.target.value)}
        className="w-full max-w-md px-4 py-2 border-2 border-red-300 rounded mb-4 focus:outline-none focus:ring-2 focus:ring-red-400"
      />
      <button
        onClick={handlePredict}
        className="bg-red-700 hover:bg-red-800 text-white px-6 py-2 rounded-full transition"
      >
        Predict
      </button>
      {result && (
        <div className="mt-6 bg-white border border-red-200 px-6 py-4 rounded-lg shadow text-lg max-w-lg text-center">
          {result}
        </div>
      )}
    </div>
  );
}

export default Predict;
